mapper.select:
SELECT s.s_name, s.s_ID FROM stadium s, regioncode rc WHERE s.s_region = rc.region_name
AND rc.region_code = #{sRegion}
AND s.s_name||s.s_region||s.s_addr||s.s_owner LIKE '%' || #{search} || '%'


SELECT S_ID, S_NAME, S_REGION, S_ADDR, S_OWNER, S_PHONE, S_NUM
FROM STADIUM
WHERE S_ID = #{sID}

SELECT g_id, G_TEAMID, G_TITLE, G_TAG, G_CREATEDATE, G_TIME, G_MINMEMBER, G_RESDATE, G_STATUS, G_USER_ID, S_ID, S_NUM, T_NAME, T_LOGO, T_REGION, T_PHONE, RANKSCORE, T_MINAGE, T_MAXAGE, T_MAXMEMBER, T_MEMBER, T_INFO, S_NAME, S_REGION, S_ADDR, S_OWNER, S_PHONE FROM ( SELECT g.G_ID, g.G_TEAMID, g.G_TITLE, g.G_TAG, g.G_CREATEDATE, g.G_TIME, g.G_MINMEMBER, g.G_RESDATE, g.G_STATUS, g.T_ID AS G_USER_ID, g.S_ID, g.S_NUM, t.T_NAME, t.T_LOGO, t.T_REGION, t.T_PHONE, t.RANKSCORE, t.T_MINAGE, t.T_MAXAGE, t.T_MAXMEMBER, t.T_MEMBER, t.T_INFO, t.CREATED_ID, t.CREATED_DATE, t.UPDATED_ID, t.UPDATED_DATE, s.S_NAME, s.S_REGION, s.S_ADDR, s.S_OWNER, s.S_PHONE, ROW_NUMBER() OVER (ORDER BY g.G_CREATEDATE) AS rn FROM GAME g INNER JOIN TEAM t ON g.G_TEAMID = t.T_ID INNER JOIN STADIUM s ON g.S_ID = s.S_ID WHERE 1=1
AND s.S_REGION = #{sRegion}
AND ( g.G_TAG = #{search} OR g.G_TITLE = #{search} OR t.T_NAME = #{search} ) AND g.G_TAG||g.G_TITLE||t.T_NAME||S_REGION||S_ADDR LIKE '%' || #{search} || '%'
AND g.T_ID = #{uID}
) WHERE rn BETWEEN (#{pageNum} + #{rowNum}*5) AND (#{pageNum} + #{rowNum}*5) + 5
